select pdx.id, pdx.pid, pdx.name, pdx.x, pdx.y from point_data_xyz pdx inner join ( select pid, max(get_time) as max_get_time from point_data_xyz where pid in
#{item}
group by pid) temp on temp.pid = pdx.pid and temp.max_get_time = pdx.get_time
