mapper.select:
select pdz.z0 as num0, pdz.total_z as totalNum, pdz.total_z_prev as totalPreNum, pdz.delt_z as deltNum, pdz.v_delt_z as vDeltNum, pdz.get_time as getTime, pdz.prev_get_time as getTimePre, pdz.pid as ptId, p.name as ptName, pg.alarm_group_id as alarmGroupId, pg.name as groupName, pm.id as missionId, pm.name as missionName from point_data_z pdz left join point p on p.id = pdz.pid left join pt_group pg on p.pt_group_id = pg.id left join pro_mission pm on pg.mission_id = pm.id left join project pj on pm.project_id = pj.id where pdz.pid in
#{item}
and pdz.stop = false
and pdz.get_time >= #{cond.startDate}
and pdz.get_time <= #{cond.endDate}
order by pdz.get_time desc

select DISTINCT get_time from point_data_z WHERE pid in
#{item}
ORDER BY get_time DESC LIMIT 5
