select pdz.id, pdz.pid, pdz.name, pdz.f from point_data_zl pdz inner join ( select pid, max(get_time) as max_get_time from point_data_zl where pid in
#{item}
group by pid) temp on temp.pid = pdz.pid and temp.max_get_time = pdz.get_time
